'use strict';
var xlsjs = require('xlsjs');
var q = require('q');


function readDocument(filename) {
    var deferred = q.defer();
    var doAsync = function(filename) {
        var xls = xlsjs.readFile(filename);
        deferred.resolve(xls);
    };
    process.nextTick(function(){
        doAsync(filename);
    });
    return deferred.promise;
}

module.exports = readDocument;